import { useState } from 'react';
import ProfileTable from './components/ProfileTable';
import ArticleTable from './components/ArticleTable'; // Birazdan oluşturacağız
import { Toaster } from 'sonner';
import { Button } from 'flowbite-react';
import { FaUser, FaNewspaper } from 'react-icons/fa';

function App() {
  const [activeTab, setActiveTab] = useState('profiles'); // 'profiles' veya 'articles'

  return (
    <div className="min-h-screen bg-orange-50 text-gray-800">
      <div className="container mx-auto py-10 px-4">
        
        {/* Başlık */}
        <h1 className="text-4xl font-bold text-center mb-8 text-orange-600 drop-shadow-sm">
          Blog Yönetim Paneli
        </h1>

        {/* Menü Butonları */}
        <div className="flex justify-center gap-4 mb-8">
          <Button 
            className={`transition-all ${activeTab === 'profiles' ? 'bg-orange-600 ring-2 ring-orange-300' : 'bg-gray-400'}`}
            onClick={() => setActiveTab('profiles')}
          >
            <FaUser className="mr-2" /> Profiller
          </Button>
          
          <Button 
            className={`transition-all ${activeTab === 'articles' ? 'bg-orange-600 ring-2 ring-orange-300' : 'bg-gray-400'}`}
            onClick={() => setActiveTab('articles')}
          >
            <FaNewspaper className="mr-2" /> Makaleler
          </Button>
        </div>

        {/* İçerik Alanı */}
        <div className="bg-white p-6 rounded-xl shadow-xl border border-orange-100">
          {activeTab === 'profiles' ? <ProfileTable /> : <ArticleTable />}
        </div>

        <Toaster position="top-right" richColors />
      </div>
    </div>
  );
}

export default App;
